import 'dotenv/config'
export default {
  components: {
    'v-dropdown': () => import('../dropdown.vue')
  },
	computed: {
		filteredAreas: function () {
			const filtered = Object.keys(this.selectedArea)
				.filter((key) => key !== '_id' && key !== '__v')
				.reduce((obj, key) => {
					obj[key] = this.selectedArea[key];
					return obj;
				}, {});
			return filtered;
		}
	},
	data() {
		return {
      snapList: [{group: '0bb6'}, {group: 'd4b2'}],
      snapSelected: '',
			apiCalling: false,
			clicked: false,
			selectedArea: '',
			activeDialog: false,
			loading: false,
			defaultValue: {
				id: '',
				name: '',
				group: '',
				x: '',
				y: '',
				z: 0,
				size: '',
				param1: 0,
				param2: 0,
				param3: '0'
			}
		};
	},
	props: {
		area: Object
	},
	methods: {
    snapSelection(e) {
      this.snapSelected = e;
    },
		formatNumber(num) {
			return parseFloat(num).toFixed(1);
		},
		async deletePosition(event) {
			event.preventDefault();
			this.loading = true;
			try {
				await this.$http.delete(`http://${this.$param.api}/api/areas/${this.selectedArea.id}`);
				this.openNotification('top-center', 'success', '👍 Succelfully updated position!', 'You can check the changes in the list');
				this.loading = false;
				this.activeDialog = false;
			} catch (err) {
				console.log('error', err);
				this.openNotification('top-center', 'danger', '💀 Something want wrong, please try again', `${err}`);
				this.loading = false;
			}
		},
		getPosition(event) {
			event.preventDefault();
			this.loading = true;
			this.$http
				.get(`http://${this.$param.api}/api/areas/snap/${this.snapSelected}`)
				.then((response) => {

          if(response.data.position)
					this.defaultValue.x = response.data.position.x;
					this.defaultValue.y = response.data.position.y;
					this.defaultValue.z = 0;
					this.openNotification('top-center', 'success', '👍 Succelfully updated position!', 'You can check the changes in the list');
					this.loading = false;
				})
				.catch((error) => {
					this.openNotification('top-center', 'danger', '💀 Something want wrong, please try again', `${error}`);
					this.loading = false;
				});
		},
    openNotification(position = null, color, title, text) {
      if(this.noti === undefined){ 
        this.noti = this.$vs.notification({
          color,
          position,
          square: true,
          title: title,
          text: text
        });
        return
      }
    if(this.noti !== null && this.noti !== undefined) {
      this.noti.close();
      this.noti = this.$vs.notification({
        color,
        position,
        square: true,
        title: title,
        text: text
      });
    }
    },
		updatePosition(obj, event) {
			event.preventDefault();
			const objJson = {
				id: obj.id,
				name: obj.name,
				group: obj.group,
				x: obj.x,
				y: obj.y,
				z: 0,
				size: obj.size,
				param1: obj.param1,
				param2: obj.param2,
				param3: obj.param3
			};
			// check if obj is same as data
			if (JSON.stringify(objJson) === JSON.stringify(this.defaultValue)) {
				this.openNotification('top-center', '#7d33ff', '😲 Hey!', 'No changes made');
				this.loading = false;
				this.activeDialog = false;
				return;
			}
			this.loading = true;
			this.dialogClose = true;
			this.$http
				.put(`http://${this.$param.api}/api/areas/${obj.id}`, this.defaultValue)
				.then((response) => {
					this.openNotification('top-center', 'success', '👍 Succelfully updated position!', 'You can check the changes in the list');
					this.loading = false;
					this.activeDialog = false;
					this.showColorPicker = false;
				})
				.catch((error) => {
					console.log(error);
					this.openNotification('top-center', 'danger', '💀 Something want wrong, please try again', `${error}`);
					this.loading = false;
					this.activeDialog = false;
					this.showColorPicker = false;
				});
		},
		closeDialog() {
			if (this.loading == true) {
				return;
			}
			this.activeDialog = false;
			this.showColorPicker = false;
		},
		openDialog(e) {
			this.defaultValue.id = e.id;
			this.defaultValue.name = e.name;
			this.defaultValue.group = e.group;
			this.defaultValue.x = e.x;
			this.defaultValue.y = e.y;
			this.defaultValue.z = 0;
			this.defaultValue.size = e.size;
			(this.defaultValue.param1 = e.param1), (this.defaultValue.param2 = e.param2), (this.defaultValue.param3 = e.param3);
			this.selectedArea = e;
			this.activeDialog = true;
		}
	}
};
